mapper.select:
select
  pk_lid, proposer, fk_proposerid, fk_verifierid, start, end, reason, fk_ltid
from d_PersonnelManagementSystem.t_user_leave


        and pk_lid = #{pkLid}


        and proposer = #{proposer}


        and fk_proposerid = #{fkProposerid}


        and fk_verifierid = #{fkVerifierid}


        and start = #{start}


        and end = #{end}


        and reason = #{reason}


        and fk_ltid = #{fkLtid}
